UPDATE customer SET file_id = 0 WHERE cnumber = old.cust_cnumber;
end
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `performance_insert_update_user_updatecusts`;
DELIMITER ;;
CREATE TRIGGER `performance_insert_update_user_updatecusts` AFTER INSERT ON `performance` FOR EACH ROW begin
declare i int;
declare j int;
DECLARE k int;
DECLARE m int;
set i = (select updatecusts from user where uid=new.user_id);
set j = (select reality from user where uid=new.user_id);
set k = (select serdata from customer where cnumber=new.pnumber);
set m = (select money from customer where cnumber=new.pnumber);
update user set updatecusts = i + 1,reality = j + new.pmoney where uid = new.user_id;
UPDATE customer set serdata = k + new.overtime,money = m + new.pmoney where cnumber = new.pnumber;
end
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `performance_delete_update_user_updatecusts`;
DELIMITER ;;
CREATE TRIGGER `performance_delete_update_user_updatecusts` AFTER DELETE ON `performance` FOR EACH ROW begin
declare i int;
declare j int;
DECLARE k int;
DECLARE m int;
set i = (select updatecusts from user where uid=old.user_id);
set j = (select reality from user where uid=old.user_id);
set k = (select serdata from customer where cnumber=old.pnumber);
set m = (select money from customer where cnumber=old.pnumber);
update user set updatecusts = i - 1,reality = j - old.pmoney where uid = old.user_id;
UPDATE customer set serdata = k - old.overtime,money = m - old.pmoney where cnumber = old.pnumber;
end
;;
DELIMITER ;
